import React, { useState } from "react";
import { Row, Col, Button, Modal, Form } from "react-bootstrap";

import API from "../../servicos/api";

export const AddListModal = (props) => {
  const [list, setList] = useState({ name: "", user_id: null })

  async function createList() {

    API.post("/list", newList)
      .then((response) => {
        setLists([...lists, newList])
        setNewList({ name: "", user_id: null })
      })
      .catch((err) => {
        console.log(err)
      })
  }

  return (
    <Modal show={props.show}>
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Nome da lista</Form.Label>
                <Form.Control
                type="text"
                onChange={(e)=>setList({...list,name: e.target.value})}
                />
            </Form.Group>
          </Form>
          <Button
            block
            type="submit"
            style={{
              backgroundColor: "#DE989A",
              border: "#DE989A",
              borderRadius: 10,
              width: 280
            }}
          >
            Feito!
          </Button>
      </Modal.Body>

    </Modal>
  )
}

export const AddItemList = (props) => {
  const [list, setList] = useState({ name: "", user_id: null })

  return (
    <Modal show={props.show}>
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary">
          Close
          </Button>
        <Button variant="primary">
          Save Changes
          </Button>
      </Modal.Footer>
    </Modal>
  )
}
