import React, { useState, useEffect, useContext } from "react";
import { Col, Row, Container, Form, Button } from "react-bootstrap";

//UseHistory
import { useHistory, Link } from "react-router-dom";
import Context from "../../App.js"
//Font Awesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheckSquare } from '@fortawesome/free-solid-svg-icons'

const Login = () => {

  const checkIcon = <FontAwesomeIcon icon={faCheckSquare} size="xs" />
  const history = useHistory();
  const contextType = React.useContext(Context)

  useEffect(() => {
    console.log(contextType)
  }, [])

  function processLogin() {
    history.push("/principal");
  }

  return (
    <Container fluid style={{ fontFamily: "Courier New" }}>



      <Row style={{ marginTop: window.innerHeight * (0.2) }}>
        <Col className="d-flex justify-content-center"
          style={{
            fontFamily: "Courier New",
            fontSize: 24,
            color: "#95AF5F",
            fontWeight: "bolder"
          }}>
          Lists
        </Col>
      </Row>


      <Row>
        <Col className="d-flex justify-content-center">
          <div style={{
            border: "solid",
            padding: 20,
            borderRadius: 5,
            borderWidth: "1.5px",
            margin: 5,
            boxShadow: "5px 3px 0px 0px"
          }}>
            <Form>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control placeholder="Usuário" style={{
                  borderColor: "#DE989A",
                  borderWidht: "1.5px",
                  width: 280,
                  outline: "none"
                }} />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control placeholder="Senha" style={{
                  borderColor: "#DE989A",
                  borderWidht: "1.5px",
                  width: 280,
                  outline: "none"
                }} />
              </Form.Group>

            </Form>
            <Button
              block
              style={{
                backgroundColor: "#DE989A",
                border: "#DE989A",
                borderRadius: 10,
                width: 280
              }}
              onClick={() => processLogin()}
            >
              Vamos lá
            </Button>

            <p
              style={{ fontSize: 11, marginTop: 20, textAlign: "center" }}
            >Como assim você ainda não tem uma conta??<br /> <Link to="/criar">Crie agora</Link></p>

          </div>
        </Col>
      </Row>

    </Container >
  )

}

export default Login;
