import React, { useState, useEffect, useContext } from "react";
import { Container, Row, Col, ListGroup } from "react-bootstrap"
import { Context } from "../../servicos/context"

//FONT AWESOME ICONS
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusCircle, faTrash, faDoorOpen } from '@fortawesome/free-solid-svg-icons'

//API
import API from "../../servicos/api";

const Principal = () => {

  const informations = useContext(Context)

  const [listName, setListName] = useState("")

  useEffect(() => { console.log(informations) }, [])

  async function createList() {

    API.post("/list", { name: listName, user_id: informations.infos.id })
      .then((response) => {

      })
      .catch((err) => {
        console.log(err)
      })

  }

  return (
    <Container fluid style={{ fontFamily: "Courier New" }}>

      <Row style={{ marginTop: 20, boxShadow: "0px 2px rgb(50,50,50,0.1)" }}>
        <Col className="d-flex justify-content-start" style={{ fontSize: 18, color: "#95AF5F", fontWeight: "bold", margin: 3 }}>Bem-vindo, {informations.infos.name}! ;)</Col>
      </Row>

      <Row style={{ marginTop: 30 }} md={12}>
        <Col md={4}>
          <div style={{
            width: 330,
            height: "100%",
            padding: 20,

          }}>
            <Row style={{ marginBottom: 10 }} >
              <Col style={{ fontSize: 24, color: "white", backgroundColor: "#DE989A", borderRadius: 10 }}>Listas</Col>
            </Row>

            <Row style={{ marginBottom: 10 }}>
              <Col className="d-flex justify-content-between"><FontAwesomeIcon icon={faPlusCircle} size="sm" color="#75903E" />
                <FontAwesomeIcon icon={faTrash} size="sm" color="#F05048" /></Col>
            </Row>

            <Row>
              {informations.infos.lists.length > 0 ?
                <ListGroup variant="flush">
                  {
                    informations.infos.lists.map((list) => {
                      <ListGroup.Item style={{ border: "solid", borderColor: "black", borderRadius: 80 }} action active={true}>{list.name}</ListGroup.Item>
                    })
                  }
                </ListGroup>
                :
                <ListGroup variant="flush">
                  <ListGroup.Item action>Vamos começar?! Clique em <FontAwesomeIcon icon={faPlusCircle} size="xs" color="#75903E" /> </ListGroup.Item>
                </ListGroup>
              }
            </Row>
          </div>
        </Col>

        <Col style={{ height: window.innerHeight * (0.8) }}>
          <div style={{
            width: "70%",
            height: "100%",
            padding: 20,
            border: "solid",
            borderRadius: 20,
            borderWidth: "1px",
            boxShadow: "5px 3px gray",
            borderColor: "#E7E7E7"
          }}>
            <Row style={{ marginBottom: 20 }} >
              <Col style={{ fontSize: 24, color: "white", backgroundColor: "#A799B7", borderRadius: 10 }}>Items da lista</Col>
            </Row>

            <Row style={{ marginBottom: 10 }}>
              <Col className="d-flex justify-content-between"><FontAwesomeIcon icon={faPlusCircle} size="lg" color="#75903E" />
              </Col>
            </Row>
          </div>
        </Col>

      </Row>
    </Container >
  )

}

export default Principal;
