import React, { useState, useEffect } from "react";
import { Col, Row, Container, Form, Button } from "react-bootstrap";

//UseHistory
import { useHistory, Link } from "react-router-dom";

//API
import API from "../../servicos/api";

const CriarUsuario = () => {

  const [message, setMessage] = useState("Hello World");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("")
  const [passwordAgain, setPasswordAgain] = useState("")

  async function createUser(){
    if (username == "") {
      setFeedback("Preencha o nome de usuário, por favor")
      return
    } else if (password == "") {
      setFeedback("Preencha a senha, por favor")
      return
    }

    await API.post("/user", { username: username, password: password })
      .then(() => {
        history.push("/");
      })
      .catch((err) => {
        if (err.reponse.message == "User already exists"){
            setFeedback("Usuário já existe")
        }else{
          setFeedback("Ocorreu um erro, tente mais tarde :l")
        }
      })
  }

  return (
    <Container fluid style={{ fontFamily: "Courier New" }}>



      <Row style={{ marginTop: window.innerHeight * (0.2) }}>
        <Col className="d-flex justify-content-center"
          style={{
            fontFamily: "Courier New",
            fontSize: 24,
            color: "#95AF5F",
            fontWeight: "bolder"
          }}>
          Lists
        </Col>
      </Row>


      <Row>
        <Col className="d-flex justify-content-center">
          <div style={{
            border: "solid",
            padding: 20,
            borderRadius: 5,
            borderWidth: "1.5px",
            margin: 5,
            boxShadow: "5px 3px 0px 0px"
          }}>
            <Form>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control placeholder="Usuário" style={{
                  borderColor: "#DE989A",
                  borderWidht: "1.5px",
                  width: 280,
                  outline: "none"
                }} />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control
                  placeholder="Senha...🤫"
                  type="password"
                  style={{
                    borderColor: "#DE989A",
                    borderWidht: "1.5px",
                    width: 280,
                    outline: "none"
                  }} />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control
                  placeholder="Confirme a senha"
                  type="password"
                  style={{
                    borderColor: "#DE989A",
                    borderWidht: "1.5px",
                    width: 280,
                    outline: "none"
                  }}
                    onChange={(e)=>setPasswordAgain(e.target.value)}
                  />
              </Form.Group>

            </Form>
            <Button
              block
              style={{
                backgroundColor: "#DE989A",
                border: "#DE989A",
                borderRadius: 10,
                width: 280
              }}
            >
              Pronto para começar!
            </Button>

            <p
              style={{ fontSize: 11, marginTop: 20, textAlign: "center" }}
            ><Link to="/">Já tenho uma com usuário e tudo mais</Link></p>

          </div>
        </Col>
      </Row>

    </Container >
  )

}

export default CriarUsuario;
