import React, { useState, useEffect, useContext } from "react";
import { Container, Row, Col, ListGroup } from "react-bootstrap"
import { Context } from "../../servicos/context"

//FONT AWESOME ICONS
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusCircle, faTrash, faDoorOpen } from '@fortawesome/free-solid-svg-icons'

//API
import API from "../../servicos/api";

const Principal = () => {

  const informations = useContext(Context)

  const [listName, setListName] = useState("")

  useEffect(() => { console.log(informations) }, [])

  async function createList() {

    API.post("/list", { name: listName, user_id: informations.infos.id })
      .then((response) => {

      })
      .catch((err) => {
        console.log(err)
      })

  }

  return (
    <Container style={{ fontFamily: "Courier New" }}>

      <Row md={12}>
        <Col className="d-flex justify-content-start" style={{ fontSize: 42, color: "#95AF5F" }}>Bem-vindo, {informations.infos.name}!</Col>
        <Col style={{ marginTop: 20 }} className="d-flex justify-content-end"><FontAwesomeIcon icon={faDoorOpen} /></Col>
      </Row>

      <Row style={{ marginTop: 30 }} md={12}>

        <Col md={4}>
          <div style={{ width: 330, height: "100%", padding: 20 }}>
            <Row style={{ marginBottom: 20 }} >
              <Col style={{ fontSize: 24, color: "white", backgroundColor: "#A799B7", borderRadius: 10 }}>Listas</Col>
            </Row>

            <Row style={{ marginBottom: 10 }}>
              <Col className="d-flex justify-content-between"><FontAwesomeIcon icon={faPlusCircle} size="lg" color="#75903E" />
                <FontAwesomeIcon icon={faTrash} size="lg" color="#F05048" /></Col>
            </Row>

            <Row>
              {informations.infos.lists.length > 0 ?
                <ListGroup variant="flush">
                  {
                    informations.infos.lists.map((list) => {
                      <ListGroup.Item action active={true}>{list.name}</ListGroup.Item>
                    })
                  }
                </ListGroup>
                :
                <ListGroup variant="flush">
                  <ListGroup.Item action>Vamos come√ßar?! Clique em + </ListGroup.Item>
                </ListGroup>
              }
            </Row>
          </div>
        </Col>

        <Col style={{ height: window.innerHeight * (0.8) }}>
          <div style={{
            width: "100%",
            height: "100%",
            padding: 20,
            borderColor: "black",
            border: "solid",
            borderRadius: 20,
            borderWidth: "1px",
            boxShadow: "5px 3px gray",
            borderColor: "#D8D8D8"
          }}>
            <Row style={{ marginBottom: 20 }} >
              <Col style={{ fontSize: 24, color: "white", backgroundColor: "#A799B7", borderRadius: 10 }}>Items da lista</Col>
            </Row>

            <Row style={{ marginBottom: 10 }}>
              <Col className="d-flex justify-content-between"><FontAwesomeIcon icon={faPlusCircle} size="lg" color="#75903E" />
                <FontAwesomeIcon icon={faTrash} size="lg" color="#F05048" /></Col>
            </Row>
          </div>
        </Col>

      </Row>
    </Container >
  )

}

export default Principal;
